import "./style.css";
function App() {
  return (
    <>
      <header className="header">
        <div className="logo">
          <img src="logo.png" alt="logo" height="68" width="68" />
          <h1>Today I learned!</h1>
        </div>
        <button className="btn btn-large btn-open">Share a fact!</button>
      </header>
      <CategoryFilter />
    </>
  );
}

function CategoryFilter() {
  return (
    <aside>
      <ul>
        <li>
          <button class="btn btn-all-categories">All</button>
        </li>
        <li>
          <button class="btn btn-category" style="background-color: #3b82f6">
            Technology
          </button>
        </li>
        <li>
          <button class="btn btn-category" style="background-color: #16a34a">
            Science
          </button>
        </li>
      </ul>
    </aside>
  );
}
export default App;

//html inside react is called JSX syntax
//in react, change html class to className
//JSX expression must only have one parent element i.e. cant have 2 child elements
